/* global.css */
:root {
  /* Colors - Primary */
  --color-primary-500: #0d8f5f;
  --color-primary-600: #0a7049;
  --color-primary-700: #085233;

  /* Colors - Accent */
  --color-accent-400: #849aff;
  --color-accent-500: #6b7df6;
  --color-accent-600: #5563d9;

  /* Colors - Neutral */
  --color-neutral-0: #ffffff;
  --color-neutral-300: #c5d1cb;
  --color-neutral-600: #5c7568;
  --color-neutral-700: #3e564b;
  --color-neutral-800: #2a3d34;
  --color-neutral-900: #152419;
  --color-neutral-950: #0a120d;

  /* Colors - Semantic */
  --color-success: #10b981;
  --color-warning: #f59e0b;
  --color-error: #ef4444;
  --color-info: #3b82f6;

  /* Spacing */
  --spacing-1: 0.25rem;
  --spacing-2: 0.5rem;
  --spacing-3: 0.75rem;
  --spacing-4: 1rem;
  --spacing-6: 1.5rem;
  --spacing-8: 2rem;
